        if (lm.indexOf("CD")>=0) sum -= 200;
        if (lm.indexOf("CM")>=0) sum -= 200;
        if (lm.indexOf("XC")>=0) sum -= 20;
        System.out.println(sum);
    }
    //把他们放在键值对里（HashMap），挨个遍历
    private static void method2(String lm) {
