            odd += Integer.parseInt(String.valueOf(chars[i]));
            //如果大于10
            //当前偶数
            //如果当前为最后一位,偶数啥事不干
            if (i == 0){

            }else{
                int currEven = Integer.parseInt(String.valueOf(chars[i-1]));
                //如果当前偶数的两倍>=10
                if (currEven*2 >= 10){
                    even += currEven*2-9;
                }else{
