            //多线程，加快传输速度
            new Thread(new Runnable() {
                @Override
                public void run() {
                    try{
                        //把is字节流，转换成字符流，并且使用缓冲流读取
                        BufferedReader br = new BufferedReader(new InputStreamReader(is));

                        //读取第一行GET请求的内容
                        String s = br.readLine();
                        //按空格把它分开
                        String[] s1 = s.split(" ");
